import React, { useState } from "react";
import "./App.css";

import Navbar from "./components/Navbar";
import Footer from "./components/Footer";
import Section from "./components/Section";
import Login from "./components/Login";
import Signup from "./components/Signup";
import ShowTasks from "./components/Showtasks";

function App() {
  const [showLogin, setShowLogin] = useState(false);
  const [showSignup, setShowSignup] = useState(false);

  const handleLoginClick = () => {
    setShowLogin(!showLogin);
    setShowSignup(false);
  };

  const handleSignupClick = () => {
    setShowSignup(!showSignup);
    setShowLogin(false);
  };
  const [tasks, setTasks] = useState([
    {
      id: 1,
      TaskTitle: "Complete Project",
      description: "Finish the coding project",
      dueDate: "2024-02-15",
      priority: "High",
    },
    // Add more tasks as needed
  ]);

  const handleMarkAsDone = (taskId) => {
    // Logic to mark the task as done
    // Update tasks state accordingly
    console.log(`Task marked as done: ${taskId}`);
  };

  const handleEdit = (taskId) => {
    // Logic to edit the task
    console.log(`Edit task: ${taskId}`);
  };

  const handleDelete = (taskId) => {
    // Logic to delete the task
    // Update tasks state accordingly
    console.log(`Delete task: ${taskId}`);
  };
  

  return (
    <div className="App">
      <Navbar onLoginClick={handleLoginClick} onSignupClick={handleSignupClick} />
      {showLogin ? (
        <Login />
      ) : showSignup ? (
        <Signup />
      ) : (
        <Section />
       
      )}
      <ShowTasks task={taskData} onMarkAsDone={handleMarkAsDone} onEdit={handleEdit} onDelete={handleDelete}/>
      <Footer />
    </div>
  );
}

export default App;
