import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Modal, Button, Form } from "react-bootstrap";

const EditTask = ({ showEditModal, handleCloseEditModal, selectedTask, handleUpdate }) => {
    const [editedTask, setEditedTask] = useState({
      title: selectedTask ? selectedTask.title : "",
      description: selectedTask ? selectedTask.description : "",
      priority: selectedTask ? selectedTask.priority : "high",
      dueDate: selectedTask ? selectedTask.dueDate : "",
    });
  
    // const handleUpdate = () => {
    //   // Implement the logic to update the task
    //   // Call handleUpdate(selectedTask.id, editedTask) or similar
    //   // handleCloseEditModal();
    // };
  return (
    <Modal show={showEditModal} onHide={handleCloseEditModal}>
      <Modal.Header closeButton>
        <Modal.Title>Edit Task</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group controlId="title">
            <Form.Label>Title</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter title"
              value={selectedTask.title}
              onChange={(e) =>
                setEditedTask({ ...selectedTask, title: e.target.value })
              }
              required
            />
          </Form.Group>
          <Form.Group controlId="description">
            <Form.Label>Description</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              placeholder="Enter description"
              value=" "
              required
            />
          </Form.Group>
          <Form.Group controlId="priority">
            <Form.Label>Priority</Form.Label>
            <Form.Control as="select" value=" " required>
              <option value="high">High</option>
              <option value="medium">Medium</option>
              <option value="low">Low</option>
            </Form.Control>
          </Form.Group>
          <Form.Group controlId="date">
            <Form.Label>Due Date</Form.Label>
            <Form.Control type="date" value="" required />
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleCloseEditModal}>
          Close
        </Button>
        <Button variant="primary" onClick={handleUpdate}>
          Save Changes
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default EditTask;
