import React, { useState, useEffect } from 'react'
import { BrowserRouter as Router } from "react-router-dom";
import "./App.css";

import Navbar from "./components/Navbar";
import Footer from "./components/Footer";
import Section from "./components/Section";
import Login from "./components/Login";
import Signup from "./components/Signup";
import TaskView from "./components/Taskview";
import axios from 'axios';

function App() {
  const [showLogin, setShowLogin] = useState(false);
  const [showSignup, setShowSignup] = useState(false);

  const handleLoginClick = () => {
    setShowLogin(!showLogin);
    setShowSignup(false);
  };

  const handleSignupClick = () => {
    setShowSignup(!showSignup);
    setShowLogin(false);
  };  
  

  
  const fetchData = async () => {
  try {
     response = await axios.get('http://127.0.0.1:8000/api/user/get-all-task', {
      headers: {
        'accept': 'application/json'
      }
    });

    if (response.status === 200) {
      console.log("success",response.tasks);
    } else {
      console.log("Something went wrong");
      
    }
  } catch (error) {
    console.error("Error adding task:", error);
  }
};



  const [tasks, setTasks] = response.tasks 

  const handleEdit = (index) => {
    console.log('Edit task at index:', index);
  };

  const handleDelete = (index) => {
    const updatedTasks = [...tasks];
    updatedTasks.splice(index, 1);
    setTasks(updatedTasks);
  };


  return (
    <Router>
      <div className="App">
        <Navbar onLoginClick={handleLoginClick} onSignupClick={handleSignupClick} />
        {showLogin ? (
          <Login />
        ) : showSignup ? (
          <Signup />
        ) : (
          <Section />
        )}
        <TaskView tasks={tasks} handleEdit={handleEdit} handleDelete={handleDelete} />
        <Footer />
      </div>
    </Router>
  );
}

export default App;
