import React, { useState } from 'react';
import './App.css';
import Signup from './components/Signup';
import Login from './components/Login';

function App() {
  const [showLogin, setShowLogin] = useState(false);
  const [showSignup, setShowSignup] = useState(false);

  const handleLoginClick = () => setShowLogin(!showLogin);
  const handleSignupClick = () => setShowSignup(!showSignup);

  return (
    <div className="App">
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <a className="navbar-brand" href="#">
          <img src="..\public\brandly-logo.png" alt="Logo" height="30" />
        </a>
        <div className="navbar-collapse justify-content-end">
          <ul className="navbar-nav">
            <li className="nav-item">
              <button className="btn nav-link" onClick={handleLoginClick}>
                Login
              </button>
            </li>
            <li className="nav-item">
              <button className="btn nav-link" onClick={handleSignupClick}>
                Sign Up
              </button>
            </li>
          </ul>
        </div>
      </nav>

      <section className="container hero-section mt-4">
        <h1>Welcome to Task Management</h1>
        <button className="btn btn-primary mt-3">Get Started</button>
      </section>

      <footer className="mt-5 text-center">
        <p>&copy; 2023 Developed by Ganesh Mane</p>
      </footer>

      {/* Render the Login and Signup components conditionally */}
      {showLogin && <Login />}
      {showSignup && <Signup />}
    </div>
  );
}

export default App;
