// Task.jsx
import React from 'react';

const ShowTasks = ({ task, onMarkDone, onDelete, onEdit }) => {
  const { title, dueDate, description, priority, done } = ShowTasks;

  return (
    <tr>
      <td>{title}</td>
      <td>{dueDate}</td>
      <td>{description}</td>
      <td>{priority}</td>
      <td>{done ? 'Done' : 'Pending'}</td>
      <td>
        {!done && (
          <button className="btn btn-success" onClick={() => onMarkDone(task.id)}>
            Mark Done
          </button>
        )}
      </td>
      <td>
        <button className="btn btn-danger" onClick={() => onDelete(task.id)}>
          Delete
        </button>
      </td>
      <td>
        <button className="btn btn-primary" onClick={() => onEdit(task.id)}>
          Edit
        </button>
      </td>
    </tr>
  );
};

export default ShowTasks;
