import React, { useState } from 'react';
import './Task.css';


const Task = ({ taskData, onMarkAsDone, onEdit, onDelete }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editedTitle, setEditedTitle] = useState(taskData.title);
  const [editedDescription, setEditedDescription] = useState(taskData.description);
  const [editedDueDate, setEditedDueDate] = useState(taskData.dueDate);
  const [editedPriority, setEditedPriority] = useState(taskData.priority);

  const handleEdit = () => {
    if (isEditing) {
      // Save edited task
      onEdit({
        id: taskData.id,
        title: editedTitle,
        description: editedDescription,
        dueDate: editedDueDate,
        priority: editedPriority,
      });
    }
    setIsEditing(!isEditing);
  };

  return (
    <div className="task-container">
      <div className="task-header">
        <h2>{isEditing ? <input type="text" value={editedTitle} onChange={(e) => setEditedTitle(e.target.value)} /> : taskData.title}</h2>
        <div className="task-actions">
          <a href="#" onClick={() => onMarkAsDone(taskData.id)}>Mark as Done</a>
          <a href="#" onClick={handleEdit}>{isEditing ? 'Save' : 'Edit'}</a>
          <a href="#" onClick={() => onDelete(taskData.id)}>Delete</a>
        </div>
      </div>
      <div className="task-details">
        <p>{isEditing ? <textarea value={editedDescription} onChange={(e) => setEditedDescription(e.target.value)} /> : taskData.description}</p>
        <p>Due Date: {isEditing ? <input type="date" value={editedDueDate} onChange={(e) => setEditedDueDate(e.target.value)} /> : taskData.dueDate}</p>
        <p>Priority: {isEditing ? (
          <select value={editedPriority} onChange={(e) => setEditedPriority(e.target.value)}>
            <option value="High">High</option>
            <option value="Medium">Medium</option>
            <option value="Low">Low</option>
          </select>
        ) : taskData.priority}</p>
      </div>
    </div>
  );
};

export default Task;
