import React from "react";
import "./Signup.css"

function Singup(){

  const submitLogin = async () => {
    fetch('http://127.0.0.1:8000/auth/register', {
      method: 'POST',
      headers: {
        'accept': 'application/json',
        'Content-Type': 'application/json'
      },
      
      body: JSON.stringify({
        'name': 'string',
        'username': 'user@example.com',
        'password': 'string',
        'confirm_password': 'string'
      })
    });
    then(response => response.json())
    .then((response) => {})
    if (response.ok) {
      // Login successful
      // Display a welcome message or perform any other action
      document.getElementById("success-message").innerHTML = "Welcome!";
      document.getElementById("success-message").classList.remove("hidden");
    } else {
      // Login failed
      // const data = await response.json();
      setErrorMessage(data.detail);
      document.getElementById("error-message").classList.remove("hidden");
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    submitLogin();
  };





    return (
<div class="main_div">
  <div class="title">Sign Up</div>
  <div id="success-message" class="hidden"></div>
  <div id="error-message" class="hidden"></div>

  <form method="post" id="sign-up-form">
    <div class="input_box">
      <label for="name">Name</label>
      <input type="text" placeholder="Enter your name" id="name" name="name" />
    </div>
    <div class="input_box">
      <label for="username">Username</label>
      <input
        type="email"
        placeholder="Enter a username"
        id="username"
        name="username"
      />
    </div>
    <div class="input_box">
      <label for="pass">Passoword</label>
      <input
        type="password"
        placeholder="Enter a password"
        id="pass"
        name="password"
      />
      <div class="icon" id="password_toggle">
        <i class="bx bx-show" id="show-pass-icon"></i>
        <i class="bx bxs-hide" id="hide-pass-icon"></i>
      </div>
    </div>
    <div class="input_box">
      <label for="re-pass">Confirm Passoword</label>
      <input
        type="password"
        placeholder="Confirm password"
        id="re-pass"
        name="re-password"
      />
      <div class="icon" id="password_toggle">
        <i class="bx bx-show" id="show-re-pass-icon"></i>
        <i class="bx bxs-hide" id="hide-re-pass-icon"></i>
      </div>
    </div>
    <div class="btn-submit">
      <button type="submit" class="btn">Sign Up</button>
    </div>
  </form>
</div>
    );
}
export default Singup;
