import React, { useState } from 'react';
import './Task.css';

const Task = ({ task }) => {
  const [isEditing, setEditing] = useState(false);
  const [editedTask, setEditedTask] = useState(task);

  const handleEdit = () => {
    setEditing(true);
  };

  const handleSave = () => {
    // Implement your save logic here
    // For simplicity, we'll just log the edited task
    console.log('Edited Task:', editedTask);
    setEditing(false);
  };

  const handleDelete = () => {
    // Implement your delete logic here
    console.log('Task deleted:', task);
  };

  const handleMarkAsDone = () => {
    // Implement your mark as done logic here
    console.log('Task marked as done:', task);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedTask({ ...editedTask, [name]: value });
  };

  const tasksData = [
    {
      id: 1,
      title: 'Complete React Project',
      description: 'Finish the coding tasks for the React project.',
      dueDate: '2024-02-15',
      priority: 'High',
    },
    {
      id: 2,
      title: 'Review Code',
      description: 'Review and provide feedback on the pull requests.',
      dueDate: '2024-02-20',
      priority: 'Medium',
    },
    {
      id: 3,
      title: 'Bug Fixes',
      description: 'Address and fix the reported bugs in the application.',
      dueDate: '2024-02-25',
      priority: 'Low',
    },
  ];

  return (
    <div className="task-container">
      <div className="task-header">
        <h3>{isEditing ? <input type="text" name="title" value={editedTask.title} onChange={handleInputChange} /> : task.title}</h3>
        <span>{task.dueDate}</span>
      </div>
      <p>{isEditing ? <textarea name="description" value={editedTask.description} onChange={handleInputChange} /> : task.description}</p>
      <div className="task-footer">
        <select name="priority" value={editedTask.priority} onChange={handleInputChange} disabled={!isEditing}>
          <option value="High">High</option>
          <option value="Medium">Medium</option>
          <option value="Low">Low</option>
        </select>
        {isEditing ? (
          <>
            <button onClick={handleSave}>Save</button>
          </>
        ) : (
          <>
            <a href="#" onClick={handleMarkAsDone}>Mark as Done</a>
            <a href="#" onClick={handleEdit}>Edit</a>
            <a href="#" onClick={handleDelete}>Delete</a>
          </>
        )}
      </div>
    </div>
  );
};

export default Task;
