import React from 'react';

const ShowTasks = ({onMarkDone, onDelete, onEdit }) => {
  const { title, dueDate, description, priority, done } = task;

  // Function to get priority color based on priority level
  const getPriorityColor = () => {
    switch (priority) {
      case 'high':
        return 'red';
      case 'medium':
        return 'yellow';
      case 'low':
        return 'green';
      default:
        return 'inherit';
    }
  };

  return (
    <tr>
      <td style={{ color: getPriorityColor() }}>{title}</td>
      <td>{dueDate}</td>
      <td>{description}</td>
      <td style={{ color: getPriorityColor() }}>{priority}</td>
      <td>{done ? 'Done' : 'Pending'}</td>
      <td>
        {!done && (
          <a href="#" className="btn btn-success" onClick={() => onMarkDone(task.id)}>
            Mark Done
          </a>
        )}
      </td>
      <td>
        <a href="#" className="btn btn-primary" onClick={() => onEdit(task.id)}>
          Edit
        </a>
      </td>
      <td>
        <a href="#" className="btn btn-danger" onClick={() => onDelete(task.id)}>
          Delete
        </a>
      </td>
    </tr>
  );
};

export default ShowTasks;
