import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Modal, Button, Form } from 'react-bootstrap';

const TaskView = ({ tasks, handleEdit, handleDelete, handleUpdate }) => {
  const [showEditModal, setShowEditModal] = useState(false);
  const [editedTask, setEditedTask] = useState({
    id: null,
    title: '',
    desc: '',
    due_date: '',
    priorities: 'high', // Set the default priority, adjust as needed
  });

  const handleEditClick = (task) => {
    setEditedTask(task);
    setShowEditModal(true);
  };

  const handleCloseEditModal = () => {
    setShowEditModal(false);
    setEditedTask({
      id: null,
      title: '',
      desc: '',
      due_date: '',
      priorities: 'high',
    });
  };

  const handleSaveEdit = () => {
    handleUpdate(editedTask);
    handleCloseEditModal();
  };

  return (
    <div className="container mt-4">
      <h2 style={{textAlign: 'center', backgroundColor:"#1565B3",color:"white",fontSize:"2.5rem", padding:"0.5rem"}}>Task List</h2>
      <table className="table mt-4">
        <thead>
          <tr>
            <th>Title</th>
            <th>Description</th>
            <th>Due Date</th>
            <th>Priority</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task, index) => (
            <tr key={index}>
              <td>{task.title}</td>
              <td>{task.desc}</td>
              <td>{task.due_date}</td>
              <td>
                <select className="form-control" value={task.priorities} disabled>
                  <option value="high">High</option>
                  <option value="medium">Medium</option>
                  <option value="low">Low</option>
                </select>
              </td>
              <td>
                <a href="#" onClick={() => handleEditClick(task)} className="btn btn-primary">
                  Edit
                </a>
                <a href="#" onClick={() => handleDelete(task.id)} className="btn btn-danger">
                  Delete
                </a>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Edit Modal */}
      <Modal show={showEditModal} onHide={handleCloseEditModal}>
        <Modal.Header closeButton>
          <Modal.Title>Edit Task</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="title">
              <Form.Label>Title</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter title"
                value={editedTask.title}
                onChange={(e) => setEditedTask({ ...editedTask, title: e.target.value })}
                required
              />
            </Form.Group>
            <Form.Group controlId="description">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                placeholder="Enter description"
                value={editedTask.desc}
                onChange={(e) => setEditedTask({ ...editedTask, desc: e.target.value })}
                required
              />
            </Form.Group>
            <Form.Group controlId="priority">
              <Form.Label>Priority</Form.Label>
              <Form.Control
                as="select"
                value={editedTask.priorities}
                onChange={(e) => setEditedTask({ ...editedTask, priorities: e.target.value })}
                required
              >
                <option value="high">High</option>
                <option value="medium">Medium</option>
                <option value="low">Low</option>
              </Form.Control>
            </Form.Group>
            <Form.Group controlId="date">
              <Form.Label>Due Date</Form.Label>
              <Form.Control
                type="date"
                value={editedTask.due_date}
                onChange={(e) => setEditedTask({ ...editedTask, due_date: e.target.value })}
                required
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseEditModal}>
            Close
          </Button>
          <Button variant="primary" onClick={handleSaveEdit}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default TaskView;
