import React, { useState } from "react";
import axios from 'axios';
import "./Login.css";
var request = require('request');

function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  
  const submitLogin = async () => {
    const requestOptions = {
      method: "POST",
      headers: { "Content-Type": "application/x-www-form-urlencoded" },
      body: JSON.stringify(
        `grant_type=&username=${username}&password=${password}&scope=&client_id=&client_secret=`
      ),
    };

    const response = await fetch("/api/token", requestOptions);
    const data = await response.json();
    console.log(data)

   
    if (response.ok) {
      // Login successful
      // Display a welcome message or perform any other action
      // document.getElementById("success-message").innerHTML = "Welcome!";
      // document.getElementById("success-message").classList.remove("hidden");
      console.log("success")
    } else {
      // Login failed
      const data = await response.json();
      setErrorMessage(data.detail);
      document.getElementById("error-message").classList.remove("hidden");
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    submitLogin();
  };

  return (
    <div className="main_div">
      <div className="title">Login</div>
      <div id="success-message" className="hidden"></div>
      <div id="error-message" className="hidden">
        {errorMessage}
      </div>

      <form onSubmit={handleSubmit} id="login-form">
        <div className="input_box">
          <label htmlFor="username">Username</label>
          <input
            type="email"
            placeholder="Enter a username"
            id="username"
            name="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div className="input_box">
          <label htmlFor="pass">Password</label>
          <input
            type="password"
            placeholder="Enter a password"
            id="pass"
            name="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div className="btn-submit option_div">
          <button type="submit" className="btn">
            Login
          </button>
        </div>
        <div className="signup_div">
          Don't have an account?
          <a href="/register">Sign Up</a>
        </div>
      </form>
    </div>
  );
}

export default Login;
