import React, { useState, useEffect } from "react";
import "./Navbar.css";

const Navbar = () => {
  const [showLogin, setShowLogin] = useState(false);
  const [showSignup, setShowSignup] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userName, setUserName] = useState("");

  useEffect(() => {
    // Check if the user is logged in based on your authentication logic
    const accessToken = localStorage.getItem("access_token");
    setIsLoggedIn(accessToken !== null);

    // Fetch user's name if logged in
    if (isLoggedIn) {
      fetchUserData();
    }
  }, [isLoggedIn]);

  const fetchUserData = async () => {
    try {
      const response = await fetch("http://127.0.0.1:8000/user", {
        method: "GET",
        credentials: "include", // Include credentials for cross-origin requests
      });

      if (response.ok) {
        const data = await response.json();
        setUserName(data.name);
      }
    } catch (error) {
      console.error("Error fetching user data:", error);
    }
  };

  const handleLoginClick = () => {
    setShowLogin(!showLogin);
    setShowSignup(false);
  };

  const handleSignupClick = () => {
    setShowSignup(!showSignup);
    setShowLogin(false);
  };

  const handleLogout = () => {
    // Implement your logout logic here
    setIsLoggedIn(false);
    setUserName(""); // Clear user's name
  };

  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <a className="navbar-brand" href="/">
          Auto<span>t</span>ask
        </a>
        <div className="navbar-collapse justify-content-end">
          {isLoggedIn ? (
            <>
              <p className="navbar-text">{userName}</p>
              <button className="btn nav-link" onClick={handleLogout}>
                Logout
              </button>
            </>
          ) : (
            <ul className="navbar-nav">
              <li className="nav-item">
                <button className="btn nav-link" onClick={handleLoginClick}>
                  Login
                </button>
              </li>
              <li className="nav-item">
                <button className="btn nav-link" onClick={handleSignupClick}>
                  Sign Up
                </button>
              </li>
            </ul>
          )}
        </div>
      </nav>
    </>
  );
};

export default Navbar;
