import React, { useState } from 'react';
import './Showtasks.css'; // Import the CSS file for styling

const TaskComponent = ({ task, onMarkAsDone, onEdit, onDelete }) => {
  const [isDone, setIsDone] = useState(false);

  const handleMarkAsDone = () => {
    setIsDone(true);
    onMarkAsDone(task.id);
  };

  return (
    <div className={`task ${isDone ? 'done' : ''}`}>
      <h2>TaskTitle: {task.TaskTitle}</h2>
      <p>{task.description}</p>
      <p>Due Date: {task.dueDate}</p>
      <p className={`priority ${task.priority.toLowerCase()}`}>{task.priority}</p>
      
      <div className="actions">
        {!isDone && <a href="#" onClick={handleMarkAsDone}>Mark as Done</a>}
        <a href="#" onClick={() => onEdit(task.id)}>Edit</a>
        <a href="#" onClick={() => onDelete(task.id)}>Delete</a>
      </div>
    </div>
  );
};

export default TaskComponent;
