import React, { useState } from "react";
import "./Navbar.css";
import Login from "./Login";  // Importing Login component
import Signup from "./Signup";  // Importing Signup component

const Navbar = () => {
  const [showLogin, setShowLogin] = useState(false);
  const [showSignup, setShowSignup] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLoginClick = () => {
    setShowLogin(!showLogin);
    setShowSignup(false);
  };

  const handleSignupClick = () => {
    setShowSignup(!showSignup);
    setShowLogin(false);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
  };

  const handleLoginSuccess = () => {
    setIsLoggedIn(true);
    setShowLogin(false);
    setShowSignup(false);
  };

  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <a className="navbar-brand" href="/">
          Auto<span>t</span>ask
        </a>
        <div className="navbar-collapse justify-content-end">
          {isLoggedIn ? (
            <>
              <p className="navbar-text">Welcome</p>
              <button className="btn nav-link" onClick={handleLogout}>
                Logout
              </button>
            </>
          ) : (
            <ul className="navbar-nav">
              <li className="nav-item">
                <button className="btn nav-link" onClick={handleLoginClick}>
                  Login
                </button>
              </li>
              <li className="nav-item">
                <button className="btn nav-link" onClick={handleSignupClick}>
                  Sign Up
                </button>
              </li>
            </ul>
          )}
        </div>
      </nav>

      {showLogin && <Login onLoginSuccess={handleLoginSuccess} />}
      {showSignup && <Signup onSignupSuccess={handleLoginSuccess} />}
    </>
  );
};

export default Navbar;
